import { visit } from "unist-util-visit";
import type { Element } from "hast";

export function rehypeNextImage() {
  return (tree: Element) => {
    visit(tree, "element", (node: Element) => {
      if (node.tagName === "img") {
        // Convert img to a custom component that will be handled by MDX
        node.tagName = "Image";
        node.properties = {
          ...node.properties,
          loading: "lazy",
          placeholder: "blur",
          blurDataURL:
            "data:image/jpeg;base64,",
        };
      }
    });
  };
}
